mapper.update:
update
task_table
SET status = #{status}, endTime = #{endTime}, testCaseDetail =
#{testCaseDetail}
WHERE id = #{id}

mapper.select:
select * from task_table 


		userId = #{userId}


		and appName ~* #{appName}


		and status = #{status}


		and providerId = #{providerId}


		and appVersion = #{appVersion}


ORDER BY createTime DESC
